class s{constructor(){this.canvas=document.getElementById("snow-canvas"),this.canvas&&(this.ctx=this.canvas.getContext("2d"),this.flakes=[],this.resize(),window.addEventListener("resize",()=>this.resize()),this.init(),this.animate())}resize(){this.width=window.innerWidth,this.height=window.innerHeight,this.canvas.width=this.width,this.canvas.height=this.height}init(){for(let t=0;t<100;t++)this.flakes.push({x:Math.random()*this.width,y:Math.random()*this.height,radius:Math.random()*3+1,density:Math.random()*100,speed:Math.random()*1+.5,opacity:Math.random()*.5+.2})}draw(){this.ctx.clearRect(0,0,this.width,this.height),this.ctx.fillStyle="rgba(255, 255, 255, 0.8)",this.ctx.beginPath();for(let i=0;i<this.flakes.length;i++){const t=this.flakes[i];this.ctx.moveTo(t.x,t.y),this.ctx.arc(t.x,t.y,t.radius,0,Math.PI*2,!0)}this.ctx.fill(),this.update()}update(){for(let i=0;i<this.flakes.length;i++){const t=this.flakes[i];t.y+=t.speed,t.x+=Math.sin(t.y/30)*.5,t.y>this.height&&(this.flakes[i]={x:Math.random()*this.width,y:-10,radius:t.radius,density:t.density,speed:t.speed,opacity:t.opacity})}}animate(){this.draw(),requestAnimationFrame(()=>this.animate())}}const h=()=>{new s};export{h as i};
